setwd("/path/to/wd")

library(Deducer) 


KOdata <- read.table("KOdataframe_git.tsv", header = TRUE, row.names = 1)

KOdatatnotpresabs<-as.data.frame(KOdata)
presabsKO<-as.matrix((KOdatatnotpresabs > 0) + 0)

sumstats<-read.csv("combinedsumstatshostregionsformatching.csv", row.names = 1, header=T)

BZ1<-c(which(sumstats[,1]=="BZ1"))
BZ2<-c(which(sumstats[,1]=="BZ2"))
campana<-c(which(sumstats[,1]=="campana"))
encrusting<-c(which(sumstats[,1]=="encrusting"))
felix<-c(which(sumstats[,1]=="felix"))
massB<-c(which(sumstats[,1]=="massB"))
massiveAgreen<-c(which(sumstats[,1]=="massiveAgreen"))
massiveApink<-c(which(sumstats[,1]=="massiveApink"))
nat<-c(which(sumstats[,1]=="nat"))
ramosa<-c(which(sumstats[,1]=="ramosa"))
reteplana<-c(which(sumstats[,1]=="reteplana"))
spa<-c(which(sumstats[,1]=="spa"))
strobilina<-c(which(sumstats[,1]=="strobilina"))

BZ1stats<-sumstats[BZ1,]
BZ2stats<-sumstats[BZ2,]
campanastats<-sumstats[campana,]
encrustingstats<-sumstats[encrusting,]
felixstats<-sumstats[felix,]
massBstats<-sumstats[massB,]
massiveAgreenstats<-sumstats[massiveAgreen,]
massiveApinkstats<-sumstats[massiveApink,]
natstats<-sumstats[nat,]
ramosastats<-sumstats[ramosa,]
reteplanastats<-sumstats[reteplana,]
spastats<-sumstats[spa,]
strobilinastats<-sumstats[strobilina,]

row.names(BZ1stats)
row.names(BZ2stats)
row.names(campanastats)
row.names(encrustingstats)
row.names(felixstats)
row.names(massBstats)
row.names(massiveAgreenstats)
row.names(massiveApinkstats)
row.names(strobilinastats)
row.names(ramosastats)
row.names(reteplanastats)
row.names(spastats)
row.names(natstats)

BZ1rowsinKOmatrix<-which(row.names(presabsKO) %in% row.names(BZ1stats))
BZ2rowsinKOmatrix<-which(row.names(presabsKO) %in% row.names(BZ2stats))
campanarowsinKOmatrix<-which(row.names(presabsKO) %in% row.names(campanastats))
encrustingrowsinKOmatrix<-which(row.names(presabsKO) %in% row.names(encrustingstats))
felixrowsinKOmatrix<-which(row.names(presabsKO) %in% row.names(felixstats))
massBrowsinKOmatrix<-which(row.names(presabsKO) %in% row.names(massBstats))
massiveAgreenrowsinKOmatrix<-which(row.names(presabsKO) %in% row.names(massiveAgreenstats))
massiveApinkrowsinKOmatrix<-which(row.names(presabsKO) %in% row.names(massiveApinkstats))
ramosarowsinKOmatrix<-which(row.names(presabsKO) %in% row.names(ramosastats))
reteplanarowsinKOmatrix<-which(row.names(presabsKO) %in% row.names(reteplanastats))
sparowsinKOmatrix<-which(row.names(presabsKO) %in% row.names(spastats))
natrowsinKOmatrix<-which(row.names(presabsKO) %in% row.names(natstats))
strobilinarowsinKOmatrix<-which(row.names(presabsKO) %in% row.names(strobilinastats))


#omit empty columns

KOpresabsdatatazerocolomitted<-presabsKO[, colSums(presabsKO != 0) > 0]
KOdatatnotpresabszerocolomitted<-KOdatatnotpresabs[, colSums(KOdatatnotpresabs != 0) > 0]

#now read in number of proteins per genome and normalize KO dataframe
genomeproteins <- read.table("proteindataframe_git.tsv", header = TRUE, row.names = 1)
genomeproteins<-as.data.frame(genomeproteins)
genomeproteinsnolowcomprows.newsorted<-genomeproteins[match(rownames(KOdatatnotpresabszerocolomitted), rownames(genomeproteins)), ]
KOnoramlizedzeroomit.newsorted<-KOdatatnotpresabszerocolomitted/genomeproteinsnolowcomprows.newsorted$proteins


#perform permutational t.tests and put dataframes together for plotting for each host species

spongerows<-strobilinarowsinKOmatrix
tararows<-natrowsinKOmatrix
numsponge<-length(spongerows)
numtara<-length(tararows)
sortedtaraontop<-c(tararows,spongerows)
sorted.Tara.Sponges.KO.presabs<-KOpresabsdatatazerocolomitted[sortedtaraontop,]
sorted.Tara.Sponges.KO.notpresabs<-KOnoramlizedzeroomit.newsorted[sortedtaraontop,]

#need to re-omit zeros here since the matrix is subsetted
sorted.Tara.Sponges.KO.presabs<-sorted.Tara.Sponges.KO.presabs[, colSums(sorted.Tara.Sponges.KO.presabs != 0) > 0]
sorted.Tara.Sponges.KO.notpresabs<-sorted.Tara.Sponges.KO.notpresabs[, colSums(sorted.Tara.Sponges.KO.notpresabs != 0) > 0]

#and now calculate the ratio columns
#first by proportion
TaraColTotalspresabs<-colSums(sorted.Tara.Sponges.KO.presabs[1:numtara,])
proportionsofMAGsTaraKOpresabs<-TaraColTotalspresabs/numtara
max(proportionsofMAGsTaraKOpresabs)

SpongeColTotalspresabs<-colSums(sorted.Tara.Sponges.KO.presabs[(numtara+1):(numtara+numsponge),])
proportionsofMAGsSpongeKOpresabs<-SpongeColTotalspresabs/numsponge
max(proportionsofMAGsSpongeKOpresabs)

combinedSpongeAndTaraproportionsofMAGs<-cbind(proportionsofMAGsSpongeKOpresabs,proportionsofMAGsTaraKOpresabs)
plot(combinedSpongeAndTaraproportionsofMAGs)

#and now let's do by gene copy
TaraColTotalsGenecopy<-colSums(sorted.Tara.Sponges.KO.notpresabs[1:numtara,])
avggenecopyofMAGsTaraKO<-TaraColTotalsGenecopy/numtara
max(avggenecopyofMAGsTaraKO)

SpongeColTotalsGenecopy<-colSums(sorted.Tara.Sponges.KO.notpresabs[(numtara+1):(numtara+numsponge),])
avggenecopyofMAGsSpongeKO<-SpongeColTotalsGenecopy/numsponge
max(avggenecopyofMAGsSpongeKO)

combinedSpongeAndTaraproportionsofMAGs<-as.data.frame(cbind(proportionsofMAGsSpongeKOpresabs,proportionsofMAGsTaraKOpresabs,avggenecopyofMAGsSpongeKO,avggenecopyofMAGsTaraKO))
plot(combinedSpongeAndTaraproportionsofMAGs$avggenecopyofMAGsSpongeKO,combinedSpongeAndTaraproportionsofMAGs$avggenecopyofMAGsTaraKO)
plot(combinedSpongeAndTaraproportionsofMAGs$proportionsofMAGsSpongeKOpresabs,combinedSpongeAndTaraproportionsofMAGs$proportionsofMAGsTaraKOpresabs)

len<-length(colnames(sorted.Tara.Sponges.KO.notpresabs))

pvalues<- vector("numeric", len)
for(i in 1:len){
  obj <-  perm.t.test(sorted.Tara.Sponges.KO.notpresabs[1:numtara,i],sorted.Tara.Sponges.KO.notpresabs[(numtara+1):(numtara+numsponge),i],statistic="t",
                      alternative=c("two.sided"), midp=TRUE, B=10000)
  pvalues[i]<-as.numeric(obj$p.value)
}

pvaluesKObeforeFDR<-pvalues

#correct with BH
pvaluesFDR<-as.matrix(p.adjust(pvaluesKObeforeFDR,method="BH",n=length(pvaluesKObeforeFDR)))
combinedSpongeAndTaraproportionsofMAGswithpvals<-cbind(combinedSpongeAndTaraproportionsofMAGs,pvaluesFDR)


#now find the sig ones

onlysig<-combinedSpongeAndTaraproportionsofMAGswithpvals[which(combinedSpongeAndTaraproportionsofMAGswithpvals$pvaluesFDR <0.01),]

#only ones that are present in more than 10% of MAGs in both categories
onlysigabundant<-onlysig[-which(onlysig$proportionsofMAGsSpongeKOpresabs<0.10 & onlysig$proportionsofMAGsTaraKOpresabs<0.10), ]
plot(onlysigabundant$proportionsofMAGsSpongeKOpresabs, onlysigabundant$proportionsofMAGsTaraKOpresabs, xlim=c(0,1), ylim=c(0,1))
abline(a=0, b=1)



#and now ratios of average copy numbers

ratioofgenecopiesSpongeoverTara<-onlysigabundant$avggenecopyofMAGsSpongeKO/onlysigabundant$avggenecopyofMAGsTaraKO
ratioofgenecopiesTaraoverSponges<-onlysigabundant$avggenecopyofMAGsTaraKO/onlysigabundant$avggenecopyofMAGsSpongeKO


onlysigabundantno.infratios<-cbind(onlysigabundant,ratioofgenecopiesSpongeoverTara,ratioofgenecopiesTaraoverSponges)

#now bind the highest ratio
onlysigabundantno.infratios$maxratios<-pmax(onlysigabundantno.infratios$ratioofgenecopiesTaraoverSponges, onlysigabundantno.infratios$ratioofgenecopiesSpongeoverTara)


ratiosforsorting<-onlysigabundantno.infratios$maxratios
sort(ratiosforsorting)

onlysigabundantno.infratios.spongevstaraspbyspKO<-onlysigabundantno.infratios

#store .csv files for recall later
write.csv(onlysigabundantno.infratios.spongevstaraspbyspKO,"onlysigabundantno.infratios.strobvsnatKO.csv",row.names = T)

